import React, { useState } from 'react'
import LogoSrc from '../../../Images/Logo.png'
import { NavBarItems } from './NavBarItems.js'
import Styles from "./NavBar.module.css"
import { Link, Router, Route } from 'react-router-dom';
import { FaHeart, FaShoppingBag, FaSearch, FaBars } from "react-icons/fa"
import Dropdown from "./Dropdown";

function NavBar() {
  const [Dropdown, setDropdown] = useState(false)


  return (

    <nav className={`${Styles.NavBar} container`}>
      < img src={LogoSrc} alt="" className={Styles.Logo} />

      <ul className='nav-item'>
        {NavBarItems.map((item) => {
          // if (item.title === "Home") {
          return (
            <li
              key={item.id}
              className={item.cName}
            // onMouseEnter={() => setDropdown(true)} 
            // onMouseLeave={() => setDropdown(false)}
            >
              {item.title}
              <Router>
                <Route to={item.path}>{item.title}</Route>
              </Router>
              {Dropdown && <Dropdown />}
            </li>
          );
        })}

      </ul>


      <ul className={Styles.ActionNavBar}>
        <li> <a href="."><FaHeart fontSize={21} /></a></li>
        <li> <a href="."><FaShoppingBag fontSize={21} /></a></li>
        <li> <a href="."><FaSearch fontSize={21} /></a></li>
        <li> <a href="."><FaBars fontSize={21} /></a></li>
      </ul>

    </nav >
  )
}

export default NavBar
